import logo from "./logo.svg";
import "./App.css";
import TextField from "@mui/material/TextField";
import { React, useState } from "react";

const App = () => {
  const [searchBox, setSearchBox] = useState("");
  const [moviesList, setMoviesList] = useState([]);

  const getMovies = async(movie) => {
    const response = await fetch("https://imdb-api.com/API/Search/k_703qlath/" + movie);
    const movies = await response.json();
    console.log(movies);
    const result = movies.results;
    setMoviesList(result);
  }

  return (
    <div className="App">
      <div className="main">
        <h1></h1>
        <div className="search">
          <TextField
            id="outlined-basic"
            value={searchBox}
            variant="outlined"
            fullWidth
            label="Search"
            onChange={(word)=>{
              setSearchBox(word.target.value)
              getMovies(word.target.value);
            }}
          />
        </div>
      </div>
      {moviesList.map((item) => (
                <li key={item.id}>{item.title}</li>
            ))}
    </div>
  );
}

export default App;
