import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { React, useState } from "react";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { getMoviesList, getMovieID } from "./Controller/MoviesController";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const App = () => {
  const [searchBox, setSearchBox] = useState("");
  const [moviesList, setMoviesList] = useState([]);
  const [movieSelect, setMovieSelect] = useState({});

  console.log();
  return (
    <div className="App">
      <Navbar bg="light" expand="lg">
        <Container fluid>
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            />
            <Form className="d-flex">
              <Form.Control
                type="search"
                placeholder="Buscar"
                className="me-2"
                aria-label="Search"
                onChange={async (object) => {
                  const value = object.target.value;
                  setSearchBox(value);
                }}
                value={searchBox}
              />
              <Button
                variant="outline-success"
                onClick={async () => {
                  await getMoviesList(searchBox)
                    .then((res) => {
                      setMoviesList(res);
                    })
                    .catch((err) => {
                      console.log("Error: " + err);
                    });
                }}
              >
                Search
              </Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Row xs={1} md={3} className="g-4">
        {moviesList.map((item) => (
          <Col>
            <Card
              id={item.id}
              onClick={async (obj) => {
                const value = obj.target.id;
                if (value !== "") {
                  console.log(obj.target);
                  setSearchBox(value);
                }
              }}
            >
              <Card.Img variant="top" id={item.id} src={item.image} />
              <Card.Body id={item.id}>
                <Card.Title id={item.id}>{item.title}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default App;
