import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { React, useState } from "react";
import { MovieCard } from "./Views/MovieCard";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { getMoviesList, getMovieID } from "./Controller/MoviesController";

const App = () => {
  const [searchBox, setSearchBox] = useState("");
  const [moviesList, setMoviesList] = useState([]);
  const [movieSelect, setMovieSelect] = useState({});

  return (
    <div className="App">
      <Navbar bg="light" expand="lg">
        <Container fluid>
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            />
            <Form className="d-flex">
              <Form.Control
                type="search"
                placeholder="Buscar"
                className="me-2"
                aria-label="Search"
                onChange={async (object) => {
                  const value = object.target.value;
                  setSearchBox(value);
                }}
                value={searchBox}
              />
              <Button
                variant="outline-success"
                onClick={async () => {
                  await getMovieID(searchBox)
                    .then((res) => {
                      console.log(res)
                      setMovieSelect(res);
                    })
                    .catch((err) => {
                      console.log("Error: " + err);
                    });
                }}
              >
                Search
              </Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {moviesList.length>0 ? (moviesList.map((item) => (
        <MovieCard
        onClick={async (value) => {
          await getMoviesList(value)
            .then((res) => {
              console.log(res)
              setMoviesList(res);
            })
            .catch((err) => {
              console.log("Error: " + err);
            });
        }}
      />
      ))) : null }
    </div>
  );
};

export default App;
