const config = require('.env');

export const getMoviesList = async (movie) => {
    console.log("https://imdb-api.com/API/Search/"+config.APP_KEY+"/" + movie);
  const response = await fetch(
    "https://imdb-api.com/API/Search/"+config.APP_KEY+"/" + movie
  );
  const movies = await response.json();
  const result = movies.results;
  return result;
};

export const getMovieID = async (ID) => {
  const response = await fetch(
    "https://imdb-api.com/en/API/Ratings/"+config.APP_KEY+"/" + ID
  );
  const movies = await response.json();
  console.log(movies)
};
